import React, { useState, useRef, useEffect } from 'react';
import {
  Card,
  TextField,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  Tooltip,
  CircularProgress,
  Alert,
  Fade,
  Slide,
  Button as MUIButton
} from '@mui/material';
import { motion } from 'framer-motion';
import { ShadcnButton } from '../components/ui/button';
import Wave from 'react-wavify';

const DEPARTMENTS = ['CSE', 'ECE', 'EEE', 'MECH', 'CIVIL'];

const Register: React.FC = () => {
  const [form, setForm] = useState({
    name: '',
    email: '',
    password: '',
    department: '',
    rollNumber: '',
    faceData: '',
    fingerprintData: '',
    ssid: '',
    macAddress: '',
    latitude: '',
    longitude: ''
  });
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [geoLoading, setGeoLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [emailInvalid, setEmailInvalid] = useState(false);

  // Geolocation
  useEffect(() => {
    setGeoLoading(true);
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          setForm((f) => ({
            ...f,
            latitude: pos.coords.latitude.toString(),
            longitude: pos.coords.longitude.toString()
          }));
          setGeoLoading(false);
        },
        () => setGeoLoading(false)
      );
    } else {
      setGeoLoading(false);
    }
  }, []);

  // SSID & MAC (browser limitations, fallback to placeholder)
  useEffect(() => {
    setForm((f) => ({
      ...f,
      ssid: 'CollegeWiFi',
      macAddress: '00:1A:2B:3C:4D:5E'
    }));
  }, []);

  // Email validation
  const validateEmail = (email: string) => {
    return /^[^@\s]+@[^@\s]+\.[^@\s]+$/.test(email);
  };

  // Animations
  const shakeAnim = emailInvalid ? {
    animate: { x: [0, -10, 10, -10, 10, 0] },
    transition: { duration: 0.4 }
  } : {};

  // Handlers
  const handleChange = (e: React.ChangeEvent<HTMLInputElement | { name?: string; value: unknown }>) => {
    const { name, value } = e.target;
    setForm((f) => ({ ...f, [name!]: value }));
    if (name === 'email') {
      setEmailInvalid(!validateEmail(value as string));
    }
  };

  const handleFaceCapture = async () => {
    // Simulate camera capture
    setForm((f) => ({ ...f, faceData: btoa('face-data') }));
  };

  const handleFingerprintCapture = async () => {
    // Simulate fingerprint capture
    setForm((f) => ({ ...f, fingerprintData: btoa('fingerprint-data') }));
  };

  const validateForm = () => {
    // Check all required fields
    for (const key of ['name', 'email', 'password', 'department', 'rollNumber', 'faceData', 'fingerprintData', 'ssid', 'macAddress']) {
      if (!form[key as keyof typeof form] || String(form[key as keyof typeof form]).trim() === '') {
        setError(`Please fill the ${key} field.`);
        return false;
      }
    }
    // Email format
    if (!validateEmail(form.email)) {
      setError('Please enter a valid email address.');
      return false;
    }
    // Biometric format (simple base64 check)
    const base64Regex = /^(?:[A-Za-z0-9+\/]{4})*(?:[A-Za-z0-9+\/]{2}==|[A-Za-z0-9+\/]{3}=)?$/;
    if (!base64Regex.test(form.faceData)) {
      setError('Face data must be a valid base64 string.');
      return false;
    }
    if (!base64Regex.test(form.fingerprintData)) {
      setError('Fingerprint data must be a valid base64 string.');
      return false;
    }
    // SSID check
    if (form.ssid !== 'CollegeWiFi') {
      setError('You must be connected to the allowed campus WiFi.');
      return false;
    }
    // MAC address format
    const macRegex = /^([0-9A-Fa-f]{2}:){5}[0-9A-Fa-f]{2}$/;
    if (!macRegex.test(form.macAddress)) {
      setError('MAC address format is invalid.');
      return false;
    }
    return true;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    if (!validateForm()) return;
    setLoading(true);
    try {
      const res = await fetch(`${process.env.REACT_APP_BASE_URL}/user/register`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: form.name,
          email: form.email,
          password: form.password,
          department: form.department,
          rollNumber: form.rollNumber,
          faceData: form.faceData,
          fingerprintData: form.fingerprintData,
          ssid: form.ssid,
          macAddress: form.macAddress
        })
      });
      const data = await res.json();
      if (data.status === 'success') {
        setSuccess('Registration successful!');
      } else {
        setError(data.message || 'Registration failed');
      }
    } catch {
      setError('Network error');
    }
    setLoading(false);
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
      position: 'relative',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    }}>
      {/* Wave Animation */}
      <Wave
        fill="#c3cfe2"
        paused={false}
        options={{ height: 20, amplitude: 20, speed: 0.2, points: 3 }}
        style={{ position: 'absolute', bottom: 0, left: 0, width: '100%', zIndex: 0 }}
      />
      <motion.div
        initial={{ y: 80, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ type: 'spring', stiffness: 80 }}
        whileHover={{ y: -3, boxShadow: '0 8px 32px rgba(44,62,80,0.18)' }}
        style={{ zIndex: 1 }}
      >
        <Card
          sx={{
            minWidth: { xs: '90vw', sm: 400 },
            maxWidth: 420,
            mx: 'auto',
            boxShadow: 3,
            borderRadius: 3,
            p: 3,
            transition: 'box-shadow 0.3s, transform 0.3s',
            '&:hover': {
              boxShadow: 6,
              transform: 'translateY(-3px)'
            }
          }}
        >
          <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: 18 }}>
            {/* Name */}
            <Tooltip title="Enter your full name" arrow>
              <TextField
                label="Name"
                name="name"
                value={form.name}
                onChange={handleChange}
                required
                variant="outlined"
                fullWidth
                sx={{
                  transition: 'border-color 0.3s',
                  '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: 'blue' },
                  '& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: 'blue' }
                }}
              />
            </Tooltip>
            {/* Email */}
            <Tooltip title="Enter your college email" arrow>
              <motion.div {...shakeAnim}>
                <TextField
                  label="Email"
                  name="email"
                  value={form.email}
                  onChange={handleChange}
                  required
                  variant="outlined"
                  fullWidth
                  error={emailInvalid}
                  helperText={emailInvalid ? 'Invalid email address' : ''}
                />
              </motion.div>
            </Tooltip>
            {/* Password */}
            <Tooltip title="Choose a strong password" arrow>
              <TextField
                label="Password"
                name="password"
                type={showPassword ? 'text' : 'password'}
                value={form.password}
                onChange={handleChange}
                required
                variant="outlined"
                fullWidth
                InputProps={{
                  endAdornment: (
                    <MUIButton
                      onClick={() => setShowPassword((v) => !v)}
                      size="small"
                      sx={{ transition: 'color 0.3s', color: showPassword ? 'teal' : 'gray' }}
                    >
                      {showPassword ? '🙈' : '👁️'}
                    </MUIButton>
                  )
                }}
                sx={{
                  transition: 'border-color 0.3s',
                  '&:hover .MuiOutlinedInput-notchedOutline': { borderColor: 'teal' },
                  '& .MuiOutlinedInput-root.Mui-focused .MuiOutlinedInput-notchedOutline': { borderColor: 'teal' }
                }}
              />
            </Tooltip>
            {/* Department */}
            <Tooltip title="Select your department" arrow>
              <FormControl fullWidth required>
                <InputLabel>Department</InputLabel>
                <Select
                  name="department"
                  value={form.department}
                  onChange={(e) => handleChange({ target: { name: 'department', value: e.target.value } } as React.ChangeEvent<{ name?: string; value: unknown }>)}
                  label="Department"
                  MenuProps={{
                    TransitionComponent: Fade
                  }}
                  sx={{
                    '& .MuiMenuItem-root:hover': { background: '#e0f7fa' }
                  }}
                >
                  {DEPARTMENTS.map((dept) => (
                    <MenuItem key={dept} value={dept}>{dept}</MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Tooltip>
            {/* Roll Number */}
            <Tooltip title="Enter your roll number" arrow>
              <TextField
                label="Roll Number"
                name="rollNumber"
                value={form.rollNumber}
                onChange={handleChange}
                required
                variant="outlined"
                fullWidth
                sx={{
                  transition: 'border-width 0.3s',
                  '&:hover .MuiOutlinedInput-notchedOutline': { borderWidth: 2 }
                }}
              />
            </Tooltip>
            {/* Face Data */}
            <Tooltip title="Capture Face" arrow>
              <ShadcnButton
                type="button"
                onClick={handleFaceCapture}
                style={{
                  transform: 'scale(1)',
                  transition: 'transform 0.2s',
                  marginBottom: 4
                }}
                onMouseEnter={(e: React.MouseEvent<HTMLButtonElement>) => (e.currentTarget.style.transform = 'scale(1.07)')}
                onMouseLeave={(e: React.MouseEvent<HTMLButtonElement>) => (e.currentTarget.style.transform = 'scale(1)')}
              >
                {form.faceData ? 'Face Captured' : 'Capture Face'}
              </ShadcnButton>
            </Tooltip>
            {/* Fingerprint Data */}
            <Tooltip title="Capture Fingerprint" arrow>
              <ShadcnButton
                type="button"
                onClick={handleFingerprintCapture}
                style={{
                  transform: 'scale(1)',
                  transition: 'transform 0.2s',
                  marginBottom: 4
                }}
                onMouseEnter={(e: React.MouseEvent<HTMLButtonElement>) => (e.currentTarget.style.transform = 'scale(1.07)')}
                onMouseLeave={(e: React.MouseEvent<HTMLButtonElement>) => (e.currentTarget.style.transform = 'scale(1)')}
              >
                {form.fingerprintData ? 'Fingerprint Captured' : 'Capture Fingerprint'}
              </ShadcnButton>
            </Tooltip>
            {/* SSID */}
            <Tooltip title="Auto-detected SSID" arrow>
              <TextField
                label="SSID"
                name="ssid"
                value={form.ssid}
                disabled
                variant="outlined"
                fullWidth
              />
            </Tooltip>
            {/* MAC Address */}
            <Tooltip title="Auto-detected MAC Address" arrow>
              <TextField
                label="MAC Address"
                name="macAddress"
                value={form.macAddress}
                disabled
                variant="outlined"
                fullWidth
              />
            </Tooltip>
            {/* Location (hidden) */}
            {geoLoading ? (
              <div style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
                <CircularProgress size={20} />
                <span>Detecting location...</span>
              </div>
            ) : null}
            {/* Submit Button */}
            <ShadcnButton
              type="submit"
              style={{
                background: 'linear-gradient(90deg, #43e97b 0%, #38f9d7 100%)',
                color: '#fff',
                fontWeight: 600,
                fontSize: 16,
                padding: '10px 0',
                marginTop: 8,
                boxShadow: '0 2px 8px rgba(44,62,80,0.08)',
                borderRadius: 8,
                transition: 'transform 0.2s',
                transform: 'scale(1)'
              }}
              onMouseEnter={(e: React.MouseEvent<HTMLButtonElement>) => (e.currentTarget.style.transform = 'scale(1.05)')}
              onMouseLeave={(e: React.MouseEvent<HTMLButtonElement>) => (e.currentTarget.style.transform = 'scale(1)')}
              disabled={loading || geoLoading}
            >
              {loading ? <CircularProgress size={22} sx={{ color: '#fff' }} /> : 'Register'}
            </ShadcnButton>
            {/* Error/Success Message */}
            <Slide direction="up" in={!!error || !!success} mountOnEnter unmountOnExit>
              <Alert severity={error ? 'error' : 'success'} sx={{ mt: 2 }}>
                {error || success}
              </Alert>
            </Slide>
          </form>
        </Card>
      </motion.div>
    </div>
  );
};

export default Register;
